<style>
    *{
    transition: all 0.6s;
}

html {
    height: 100%;
    background: #FF4400 50%;
}

body{
    font-family: 'Ubuntu Mono', sans-serif;
    color: #ffffff;
    margin: 0;
}

#main{
    display: table;
    width: 100%;
    height: 100vh;
    text-align: center;
}

.fof{
	  display: table-cell;
	  vertical-align: middle;
}

.fof h1{
	  border:  5px solid white;
	  border-radius: 1rem;
	  font-size: 100px;
	  display: inline-block;
	  padding-right: 12px;

}
.fof h2{
	  font-size: 50px;
	  display: inline-block;
	  padding-right: 12px;
	  animation: type .5s alternate infinite;
}
.fof img{
	width: 150px;
	height: 150px;

}

@keyframes  type{
	  from{box-shadow: inset -3px 0px 0px #ffffff;}
	  to{box-shadow: inset -3px 0px 0px transparent;}

}</style>

<div id="main">
